import React, { useEffect, useState } from 'react';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');

  const arabicRegex = /[\u0600-\u06FF\u0750-\u077F\u08A0-\u08FF]/;

  const loadChatHistory = async () => {
    const response = await fetch('http://127.0.0.1:5000/api/history');
    console.log(`response history`, response);
    const data = await response.json();
    console.log(`data`, data);
    setMessages(data || []);
  };

  useEffect(() => {
    loadChatHistory();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://127.0.0.1:5000/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message })
    });

    const data = await response.text();
    console.log('data from submit', data);
    if (data) {
      setMessages([...messages, { role: 'user', parts: message }, { role: 'ai', parts: data }]);
      setMessage('');
    }
  };

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-100">
      <header className="bg-white shadow-md w-full text-center py-4">
        <h1 className="text-3xl font-bold">ðŸŒŸ AI Study Assistant ðŸŒŸ</h1>
      </header>
      <div className="flex flex-col w-full max-w-lg p-4 mt-6 bg-white shadow-md rounded-lg">
        <div id="messages" className="flex-1 overflow-y-auto p-4 bg-gray-50 rounded-md mb-4">
          {messages.map((msg, index) => {
            const direction = arabicRegex.test(msg.parts) ? 'rtl' : 'ltr';
            return (
              <div key={index} className={`mb-4 ${msg.role === 'user' ? 'text-right' : 'text-left'}`} dir={direction}>
                <strong>{msg.role === 'user' ? 'You' : 'AI'}:</strong>
                <div dangerouslySetInnerHTML={{ __html: msg.parts }} />
              </div>
            );
          })}
        </div>
        <form onSubmit={handleSubmit} className="flex items-center space-x-2">
          <input
            type="text"
            id="message"
            name="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={(e) => {
              console.log(`e shift key`, e.shiftKey);
              console.log(`e Enter key:`, e.key);
              if (e.key === "Enter" && !e.shiftKey) {
                e.preventDefault(); // Prevents adding a new line
                handleSubmit(); // Triggers the submit function
              }
              if (e.key === e.shiftKey) {
                e.preventDefault();
                setMessage(`${message}\n`);
              }
            }}
            className="w-full p-2 border rounded-md"
            placeholder="Type your message here..."
            required
          />
          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">
            Send
          </button>
        </form>
      </div>
      <footer className="bg-white text-center py-4 mt-auto">
        <p className="text-sm text-gray-600">Powered by DRIHMIA AI | Your Personal Study Assistant</p>
      </footer>
    </div>
  );
};

export default Chat;
