import React, { useEffect, useState, useRef } from "react";

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [prev_key, setPrevKey] = useState("");
  const messagesEndRef = useRef(null);

  const arabicRegex = /[\u0600-\u06FF\u0750-\u077F\u08A0-\u08FF]/;

  // Load chat history
  const loadChatHistory = async () => {
    const response = await fetch("http://127.0.0.1:5000/api/history");
    const data = await response.json();
    setMessages(data || []);
  };

  useEffect(() => {
    loadChatHistory();
  }, []);

  // Scroll to the bottom when messages change
  useEffect(() => {
    const scrollToBottom = () => {
      if (messagesEndRef.current) {
        messagesEndRef.current.scrollTop =
          messagesEndRef.current.scrollHeight;
      }
    };

    // Delay the scroll adjustment slightly to ensure DOM is updated
    const timeout = setTimeout(scrollToBottom, 50);

    // Cleanup timeout on unmount or when messages change again
    return () => clearTimeout(timeout);
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://127.0.0.1:5000/api/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message }),
    });

    const data = await response.text();
    if (data) {
      setMessages([
        ...messages,
        { role: "user", parts: message },
        { role: "ai", parts: data },
      ]);
      setMessage("");
    }
  };

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-100">
      <header className="bg-white shadow-md w-full text-center py-4">
        <h1 className="text-3xl font-bold">ðŸŒŸ AI Study Assistant ðŸŒŸ</h1>
      </header>
      <div className="flex flex-col w-full max-w-lg p-4 mt-6 bg-white shadow-md rounded-lg">
        <div
          ref={messagesEndRef} // Attach ref to this div
          id="messages"
          className="flex-1 overflow-y-auto p-4 bg-gray-50 rounded-md mb-4"
        >
          {messages.map((msg, index) => {
            const direction = arabicRegex.test(msg.parts) ? "rtl" : "ltr";
            return (
              <div
                key={index}
                className={`mb-4 ${
                  msg.role === "user" ? "text-right" : "text-left"
                }`}
                dir={direction}
              >
                <strong>{msg.role === "user" ? "You" : "AI"}:</strong>
                <div
                  dangerouslySetInnerHTML={{
                    __html: msg.role === "user"
                      ? msg.parts.replace("\n", "<br>")
                      : msg.parts,
                  }}
                />
              </div>
            );
          })}
        </div>
        <form
          onSubmit={handleSubmit}
          className="flex items-center space-x-2"
        >
          <textarea
            type="text"
            id="message"
            name="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter" && prev_key === "Shift") {
                e.preventDefault(); // Prevents adding a new line
                setMessage((message) => `${message}\n`);
              }
              if (e.key === "Enter" && prev_key !== "Shift") {
                e.preventDefault();
                handleSubmit(e); // Triggers the submit function
              }
              setPrevKey(e.key);
            }}
            className="w-full p-2 border rounded-md"
            placeholder="Type your message here..."
            required
          />
          <button
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
          >
            Send
          </button>
        </form>
      </div>
      <footer className="bg-white text-center py-4 mt-auto">
        <p className="text-sm text-gray-600">
          Powered by DRIHMIA AI | Your Personal Study Assistant
        </p>
      </footer>
    </div>
  );
};

export default Chat;
